WORD=`curl -v --silent "https://random-word-api.herokuapp.com/word?number=1" 2>&1 | sed 's/\[\"//g' | sed 's/\"\]//g' | tail -c 5` ; curl -v --silent "https://news.google.com/rss/search?q=$WORD&hl=en-GB&gl=GB&ceid=GB:en"  2>&1 | awk -F'item' '{print $2}' | awk -F'item' '{print $1}' | head -c 150 | tail -c 80 | sed 's/title//g' | tr -cd '[a-zA-Z0-9 ]._-' > /usr/share/nginx/html/index.html
